INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('HTML', 'Hyper Text Markup Language', 'Standard Markup language for Web development', 'HTML is the standard markup language for creating Web pages. HTML stands for Hyper Text Markup Language. HTML describes the structure of Web pages using markup. HTML elements are the building blocks of HTML pages. HTML elements are represented by tags. HTML tags label pieces of content such as "heading", "paragraph", "table", and so on. Browsers do not display the HTML tags, but use them to render the content of the page.', 'Easy', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('CSS', 'Cascading Style Sheets', 'Standard style sheet language for Web development', 'CSS is the standard style sheet language for creating Web pages. CSS stands for Cascading Style Sheets. CSS describes how HTML elements are to be displayed on screen, paper, or in other media. CSS saves a lot of work. It can control the layout of multiple web pages all at once. External stylesheets are stored in CSS files.', 'Easy', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('JavaScript', 'JavaScript', 'Standard scripting language for Web development', 'JavaScript is the standard scripting language for creating Web pages. JavaScript is an object-based scripting language that is lightweight and cross-platform. JavaScript is not the same as Java. JavaScript is easy to learn. This tutorial will teach you JavaScript from basic to advanced.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('PHP', 'PHP: Hypertext Preprocessor', 'Server-side scripting language for Web development', 'PHP is a server scripting language, and a powerful tool for making dynamic and interactive Web pages. PHP is a widely-used, free, and efficient alternative to competitors such as Microsofts ASP. PHP is a server-side scripting language. PHP scripts are executed on the server. PHP supports many databases (MySQL, Informix, Oracle, Sybase, Solid, PostgreSQL, Generic ODBC, etc.)', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Python', 'Python', 'General-purpose programming language', 'Python is a general-purpose programming language created by Guido van Rossum. Python is a high-level programming language, with applications in numerous areas, including web programming, scripting, scientific computing, and artificial intelligence. It is very popular and widely used.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Java', 'Java', 'General-purpose programming language', 'Java is a general-purpose programming language that is class-based, object-oriented, and designed to have as few implementation dependencies as possible. It is intended to let application developers write once, run anywhere (WORA), meaning that compiled Java code can run on all platforms that support Java without the need for recompilation.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('C++', 'C++', 'General-purpose programming language', 'C++ is a general-purpose programming language created by Bjarne Stroustrup as an extension of the C programming language, or "C with Classes". The language has expanded significantly over time, and modern C++ now has object-oriented, generic, and functional features in addition to facilities for low-level memory manipulation.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('C#', 'C#', 'General-purpose programming language', 'C# is a general-purpose, multi-paradigm programming language developed by Microsoft as part of its .NET initiative led by Anders Hejlsberg. The language is intended for use in developing software components suitable for deployment in distributed environments.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Ruby', 'Ruby', 'General-purpose programming language', 'Ruby is an interpreted, high-level, general-purpose programming language. It was designed and developed in the mid-1990s by Yukihiro "Matz" Matsumoto in Japan. Ruby is dynamically typed and uses garbage collection. It supports multiple programming paradigms, including procedural, object-oriented, and functional programming.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Swift', 'Swift', 'General-purpose programming language', 'Swift is a general-purpose, multi-paradigm, compiled programming language developed by Apple Inc. and the open-source community, first released in 2014. Swift was developed as a replacement for Apple''s earlier programming language Objective-C, as Objective-C had been largely unchanged since the early 1980s and lacked modern language features.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Kotlin', 'Kotlin', 'General-purpose programming language', 'Kotlin is a cross-platform, statically typed, general-purpose programming language with type inference. Kotlin is designed to be fully interoperable with Java, and the JVM version of its standard library depends on the Java Class Library, but type inference allows its syntax to be more concise.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Go', 'Go', 'General-purpose programming language', 'Go is a statically typed, compiled programming language designed at Google by Robert Griesemer, Rob Pike, and Ken Thompson. Go is syntactically similar to C, but with memory safety, garbage collection, structural typing, and CSP-style concurrency.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Rust', 'Rust', 'General-purpose programming language', 'Rust is a multi-paradigm systems programming language focused on safety, especially safe concurrency. Rust is syntactically similar to C++, but is designed to provide better memory safety while maintaining high performance.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('TypeScript', 'TypeScript', 'General-purpose programming language', 'TypeScript is an open-source programming language developed and maintained by Microsoft. It is a strict syntactical superset of JavaScript and adds optional static typing to the language. TypeScript is designed for the development of large applications and transcompiles to JavaScript.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Dart', 'Dart', 'General-purpose programming language', 'Dart is a client-optimized programming language for fast apps on any platform. Dart is an open-source, scalable programming language, with robust libraries and runtimes, for building web, server, and mobile apps.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Scala', 'Scala', 'General-purpose programming language', 'Scala is a general-purpose programming language providing support for functional programming and a strong static type system. Designed to be concise, many of Scala''s design decisions are aimed to address criticisms of Java.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Perl', 'Perl', 'General-purpose programming language', 'Perl is a family of two high-level, general-purpose, interpreted, dynamic programming languages. "Perl" refers to Perl 5, but from 2000 to 2019 it also referred to its redesigned "sister language", Perl 6, before the latter''s name was officially changed to Raku in October 2019.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Lua', 'Lua', 'General-purpose programming language', 'Lua is a lightweight, high-level, multi-paradigm programming language designed primarily for embedded use in applications. Lua is cross-platform, since the interpreter is written in ANSI C, and has a relatively simple C API.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Haskell', 'Haskell', 'General-purpose programming language', 'Haskell is a standardized, general-purpose, purely functional programming language with non-strict semantics and strong static typing. It is named after logician Haskell Curry. Haskell is based on the lambda calculus, hence the lambda we use as a logo.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('R', 'R', 'General-purpose programming language', 'R is a programming language and free software environment for statistical computing and graphics supported by the R Foundation for Statistical Computing. The R language is widely used among statisticians and data miners for developing statistical software and data analysis.', 'Hard', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('SQL', 'Structured Query Language', 'Standard language for relational database management systems', 'SQL is a standard language for storing, manipulating and retrieving data in databases. Our SQL tutorial will teach you how to use SQL in: MySQL, SQL Server, MS Access, Oracle, Sybase, Informix, Postgres, and other database systems.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('NoSQL', 'NoSQL', 'Non-relational database management systems', 'NoSQL databases are non-tabular, and store data differently than relational tables. NoSQL databases come in a variety of types based on their data model. The main types are document, key-value, wide-column, and graph.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('MongoDB', 'MongoDB', 'Document-oriented database management system', 'MongoDB is a cross-platform document-oriented database program. Classified as a NoSQL database program, MongoDB uses JSON-like documents with optional schemas. MongoDB is developed by MongoDB Inc. and licensed under the Server Side Public License (SSPL).', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Firebase', 'Firebase', 'Mobile and web application development platform', 'Firebase is a platform developed by Google for creating mobile and web applications. It was originally an independent company founded in 2011. In 2014, Google acquired the platform and it is now their flagship offering for app development.', 'Medium', '0');  
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Node.js', 'Node.js', 'JavaScript runtime environment', 'Node.js is an open-source, cross-platform, back-end JavaScript runtime environment that runs on the V8 engine and executes JavaScript code outside a web browser. Node.js lets developers use JavaScript to write command line tools and for server-side scripting—running scripts server-side to produce dynamic web page content before the page is sent to the user''s web browser.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('Express.js', 'Express.js', 'Web application framework for Node.js', 'Express.js, or simply Express, is a back end web application framework for Node.js, released as free and open-source software under the MIT License. It is designed for building web applications and APIs. It has been called the de facto standard server framework for Node.js.', 'Medium', '0');
INSERT INTO `coding_languages`(`name`, `complete_name`, `purpose`, `description`, `difficulty`, `personal_progress`) VALUES ('React', 'React', 'JavaScript library for building user interfaces', 'React (also known as React.js or ReactJS) is an open-source front-end JavaScript library for building user interfaces or UI components. It is maintained by Facebook and a community of individual developers and companies. React can be used as a base in the development of single-page or mobile applications.', 'Medium', '0');